drop database if exists fkart;
create database FKART;
USE FKART;
create table ek_customer(
    email_id varchar(50),
    name varchar(50),
    password varchar(70) not null,
    phone_number varchar(10) not null unique,
	confirm_password varchar(70) not null,
   
    constraint ek_customer_email_id_pk primary key(email_id)
);

use fkart;
CREATE TABLE ek_vendor (
  vendor_id int NOT NULL AUTO_INCREMENT,
  vendor_name varchar(255) DEFAULT NULL,
  mobile_number varchar(255) DEFAULT NULL,
  email_id varchar(255) DEFAULT NULL,
  image varchar(255) DEFAULT NULL,
  description varchar(255) DEFAULT NULL,
  password varchar(255) DEFAULT NULL,
  confirm_password varchar(255) DEFAULT NULL,
  PRIMARY KEY (vendor_id)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

use fkart;
CREATE TABLE ek_product (
  product_id int NOT NULL AUTO_INCREMENT,
  name varchar(255) DEFAULT NULL,
  description varchar(255) DEFAULT NULL,
  price double DEFAULT NULL,
quantity int DEFAULT NULL,
  image varchar(255) DEFAULT NULL,
  vendor_id int DEFAULT NULL,
  estimated_time datetime(6) DEFAULT NULL,
  PRIMARY KEY (product_id),
  KEY vendor_id (vendor_id),
  CONSTRAINT ek_product_ibfk_1 FOREIGN KEY (vendor_id) REFERENCES ek_vendor (vendor_id)
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
